import React,{Component} from 'react';

import Select from 'react-select';

import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

import CustomerForm from "./CustomerForm";


const stations = [
    { label: "Wellawatte", value: 1 },
    { label: "Bambalapitya", value: 2 },
    { label: "Kollupitya", value: 3 },
];
const  trainNumbers = [
    { label: "T001", value: 1 },
    { label: "T002", value: 2 },

];
const  trainNames = [
    { label: "WEL", value: 1 },
    { label: "KOL", value: 2 },

];


class TrainForm extends Component
{

    constructor (props) {
        super(props);
        var today = new Date();

        this.state = {
            trainDetails:{
                start:null,
                end:null,
                tNo:null,
                tName:null,
                date: today,
                tickets:''
            }

      };

        this.onDateChange = this.onDateChange.bind(this);
        this.onTicketsChange = this.onTicketsChange.bind(this);
        this.dateFormat=this.dateFormat.bind(this);


    }

    dateFormat(date){
        var dd = date.getDate();
        var mm = date.getMonth()+1; //January is 0!
        var yyyy = date.getFullYear();

        if(dd<10) {
            dd = '0'+dd
        }

        if(mm<10) {
            mm = '0'+mm
        }

        return  dd + '/' + mm + '/' + yyyy;

    }

    onDateChange(date) {
        this.setState({
            trainDetails:{date: date}
        })
    }

    onTicketsChange(e){
        this.setState({
            trainDetails:{tickets: e.target.value}
        });
    }




    render() {
        return(
            <div style={leftStyle}>
                <div style={{left:"200px",right:"300px", position:"absolute"}}>
                    <h3>Train details</h3>
                </div><br/><br/><br/>
                <table style={tableStyle}>
                    <tbody>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Starting Point</label></b>
                           </td>
                           <td>
                               <Select
                                   autosize={true}
                                   placeholder="select Starting point"
                                   options={ stations } />
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Destination Point</label></b>
                           </td>
                           <td>
                               <Select width="500px"
                                       placeholder="select Destination point"
                                       options={ stations} />
                           </td>
                       </tr>

                       <tr style={cellStyle}>
                           <td>
                               <b><label>Train Number</label></b>
                           </td>
                           <td>
                               <Select autosize={true}
                                       placeholder="select Train Numbers"
                                       options={ trainNumbers } />
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Train Name</label></b>
                           </td>
                           <td>
                               <Select autosize={true}
                                       placeholder="select Train Names"
                                       options={ trainNames } />
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Ticket Price</label></b>
                           </td>
                           <td>
                               <label>Ticket Price per person</label>
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Date</label></b>
                           </td>
                           <td>
                               <DatePicker
                                   selected={ this.state.trainDetails.date}
                                   onChange={ this.onDateChange }
                                   name="startDate"
                                   value={this.state.trainDetails.date}
                                   dateFormat={this.dateFormat(this.state.trainDetails.date)}
                               />
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Number of tickets</label></b>
                           </td>
                           <td>
                               <input type='text'
                                name="tickets"
                                value={this.state.trainDetails.tickets}
                                onChange={this.onTicketsChange}
                               />
                           </td>
                       </tr>
                    </tbody>
                    <CustomerForm  {...this.props} trainDetails={this.state.trainDetails} />
                </table>
                {
                    console.log(this.state.trainDetails)
                }
            </div>

        );
    }

}




const leftStyle={
    position:'fixed',
    margin:'auto',
    background:'LightGrey',
    width:'700px',
    height:'500px',
    textAlign:'left',
    border: '3px solid Black',
    borderTopStyle:'none',
    top: '190px',
};
const cellStyle={
    height:'50px'
};

const tableStyle={
    width:"500px",
    marginLeft:'10%',
    marginRight:'10%'
};
export default TrainForm;
