import React,{Component} from 'react';
import ConfirmSection from "./ConfirmSection";

let pDetail=[];

const Statement=(props)=>{
    console.log(this.state.Person);
    pDetail.push(props);
};

class CustomerForm extends Component
{
    constructor (props) {
        super(props);

        this.state = {

            TrainDetail: this.props.TrainDetail,

            Person:{
               nicNo:null,
               name:null,
               contactNo:null,
               email:null
            }

        }
    }

    onChange(e){
        const {Person}=this.state;
        const currentState=Person;
        const {name,value}=e.target;
        currentState[name]=value;
        this.setState({
            Person:currentState
        });

    }

    render() {
        return(
            <div style={rightStyle}>
                <div style={{left:"100px",right:"200px", position:"absolute"}}>
                    <h3 style={{textAlign:'center'}}>Customer Details</h3>
                </div><br/><br/><br/>

                <table style={tableStyle}>
                    <tbody>
                    <tr style={cellStyle}>
                        <td>
                            <b><label>Name</label></b>
                        </td>
                        <td>
                            <input
                                type='text'
                                name="Name"
                                value={this.state.Person.name}
                            />
                        </td>
                    </tr>
                    <tr style={cellStyle}>
                        <td>
                            <b><label>NIC No</label></b>
                        </td>
                        <td>
                            <input
                                type='text'
                                placeholder="NIC no should be of 10 digits"
                                name="nic"
                                value={this.state.Person.nicNo}
                            />
                        </td>

                    </tr>
                    <tr style={cellStyle}>
                        <td>
                            <b><label>Contact Number</label></b>
                        </td>
                        <td>
                            <input type='text'
                                   name="contact"
                                   value={this.state.Person.contactNo}
                            />
                        </td>
                    </tr>
                    <tr style={cellStyle}>
                        <td>
                            <b><label>Email</label></b>
                        </td>
                        <td>
                            <input
                                type='text'
                                name="email"
                                value={this.state.Person.email}
                            />
                        </td>
                    </tr>
                    </tbody>
                </table>

                {Statement(this.state.Person)}
                <ConfirmSection  {...this.props}  TrainDetail={this.state.TrainDetail}  Person={pDetail}/>
            </div>);
    }

}

const rightStyle={
    position:'absolute',
    margin:'auto',
    left:'600px',
    backgroundColor:'LightGrey',
    width:'585px',
    height:'500px',
    textAlign:'left',
    border: '3px solid Black',
    borderTopStyle:'none',
    bottom: '180px',
    top: '180px',

};
const cellStyle={
    height:'50px'
};
const tableStyle={
  width:"500px",
  marginLeft:'10%',
  marginRight:'10%'
};

export default CustomerForm;
