import React,{Component} from 'react';

import Select from 'react-select';

import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

const stations = [
    { label: "Wellawatte", value: 1 },
    { label: "Bambalapitya", value: 2 },
    { label: "Kollupitya", value: 3 },
];

class TrainDetails extends Component
{

    constructor (props) {
        super(props);
        var today = new Date();

        this.state = {
            startDate: today,
            tDetails:{
                tickets:''
           }
      };


        this.handleChange = this.handleChange.bind(this);
        this.dateFormat=this.dateFormat.bind(this);
    }
    handleChange(date) {
        this.setState({
            startDate: date
        })
    }

    onChange(e){
        // this.setState({  [e.target.name]: e.target.value }  );
       console.log(e.target.value);
        console.log(e.target.name);
        this.setState({  [e.target.name]: e.target.value }  );
    }

    dateFormat(date){
            var dd = date.getDate();
            var mm = date.getMonth()+1; //January is 0!
            var yyyy = date.getFullYear();

            if(dd<10) {
                dd = '0'+dd
            }

            if(mm<10) {
                mm = '0'+mm
            }

        return  dd + '/' + mm + '/' + yyyy;

    }


    render() {
        return(
            <div style={leftStyle}>
                <div style={{left:"200px",right:"300px", position:"absolute"}}>
                    <h3>Train details</h3>
                </div><br/><br/><br/>
                <table style={tableStyle}>
                    <tbody>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Starting Point</label></b>
                           </td>
                           <td>
                               <Select autosize={true} options={ stations } />
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Destination Point</label></b>
                           </td>
                           <td>
                               <Select width="500px" options={ stations} />
                           </td>
                       </tr>

                       <tr style={cellStyle}>
                           <td>
                               <b><label>Train Number</label></b>
                           </td>
                           <td>

                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Train Name</label></b>
                           </td>
                           <td>

                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Ticket Price</label></b>
                           </td>
                           <td>
                               <label>Ticket Price per person</label>
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Date</label></b>
                           </td>
                           <td>
                               <DatePicker
                                   selected={ this.state.startDate }
                                   onChange={ this.handleChange }
                                   name="startDate"
                                   dateFormat={this.dateFormat(this.state.startDate)}
                               />
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Number of tickets</label></b>
                           </td>
                           <td>
                               <input type='text'
                                name="tickets"
                                value={this.state.tDetails.tickets}
                                onChange={this.onChange}
                               />
                           </td>
                       </tr>
                    </tbody>
                </table>
            </div>

        );
    }
}

const leftStyle={
    position:'fixed',
    margin:'auto',
    background:'LightGrey',
    width:'700px',
    height:'500px',
    textAlign:'left',
    border: '3px solid Black',
    borderTopStyle:'none',
    top: '190px',
};
const cellStyle={
    height:'50px'
};

const tableStyle={
    width:"500px",
    marginLeft:'10%',
    marginRight:'10%'
};
export default TrainDetails;
