import React,{Component} from 'react';


class CustomerDetails extends Component
{
    constructor(props){
        super(props);
    }

    onChange(event){
        event.preventDefault();
        event.stopPropagation();
        event.target.name=event.target.value;
    }

    onSubmit(event){
        event.preventDefault();
        event.stopPropagation();

        this.props.onSubmit({Name:this.name});


    }


    render() {
        return(
            <div style={rightStyle}>
                <div style={{left:"100px",right:"200px", position:"absolute"}}>
                    <h3 style={{textAlign:'center'}}>Customer Details</h3>
                </div><br/><br/><br/>
                <form onSubmit={event => this.onSubmit(event) }>
                    <table style={tableStyle}>
                        <tbody>
                        <tr style={cellStyle}>
                            <td>
                                <b><label>Name</label></b>
                            </td>
                            <td>
                                <input
                                    type='text'
                                    name="Name"/>
                            </td>
                        </tr>
                        <tr style={cellStyle}>
                            <td>
                                <b><label>NIC No</label></b>
                            </td>
                            <td>
                                <input
                                    type='text'
                                    placeholder="NIC no should be of 10 digits"
                                    name="nic"/>
                            </td>

                        </tr>
                        <tr style={cellStyle}>
                            <td>
                                <b><label>Contact Number</label></b>
                            </td>
                            <td>
                                <input type='text' name="contact"/>
                            </td>
                        </tr>
                        <tr style={cellStyle}>
                            <td>
                                <b><label>Email</label></b>
                            </td>
                            <td>
                                <input type='text' name="email"/>
                            </td>
                        </tr>
                        </tbody>
                    </table>
                </form>
            </div>);
    }
}

const rightStyle={
    position:'fixed',
    margin:'auto',
    right:'400px',
    backgroundColor:'LightGrey',
    width:'600px',
    height:'500px',
    textAlign:'left',
    border: '3px solid Black',
    borderTopStyle:'none',
    top: '190px',
};
const cellStyle={
    height:'50px'
};
const tableStyle={
  width:"500px",
  marginLeft:'10%',
  marginRight:'10%'
};

export default CustomerDetails;
