import React from 'react';
import "./App.css";
import Header from './components/Header';
import TrainForm from './components/TrainForm';


import Bill from './Pages/Bill';
import MobilePayment from './components/CustomerForm';
import CreditCardPayment from './components/ConfirmSection';

import {Route } from 'react-router-dom';

class App extends React.Component{

    constructor(props){

        super(props);
        this.onSubmit=this.onSubmit.bind(this);
    }

    onSubmit(e){
        e.preventDefault();
        window.location.href="/bill";
        console.log(e);


    }

    render() {
       return (

               <div className="app">
                   <div style={mainStyle}>
                       <Header/>
                       <Route exact path="/" render={props=>(
                           <form  onSubmit={this.onSubmit}>
                               <TrainForm />
                           </form>
                       )}/>
                       <Route path='/bill'  component={Bill}/>
                       <Route path='/credit' component={CreditCardPayment}/>
                       <Route path='/mobile' component={MobilePayment}/>
                   </div>
               </div>

       );

   }
}
const mainStyle={
    padding:'10px',
    position: 'absolute',
    left:'100px',
    right:'220px',
    top: '100px',
    bottom: '10px'
};

export default App;
