import React,{Component} from 'react';

import axios from 'axios';

let details={};
let id='';

const submit=(mobInfo)=>{
    console.log(mobInfo);
};

class MobileView extends Component{

    constructor(props){
        super(props);
        this.state={
            TrainDetail:'',
            Person:'',
            ticketId:'',

            MobInfo:{
                phone:null,
                PIN:null
            }
        };

        id=window.location.href.substring(29,43);

        axios.get('http://localhost:5000/citizen/'+id)
            .then(res => {
                details = res.data.pop();
                console.log(details);
                this.setState({TrainDetail:details.TrainDetail} );
                this.setState({Person:details.Person} );
                this.setState({ticketId:details.id});
            });
        console.log(id);

        this.onChange.bind(this);
        this. onClick=this.onClick.bind(this);
    }

    onChange(e){
        const {MobInfo}=this.state;
        const currentState=MobInfo;
        const {name,value}=e.target;
        currentState[name]=value;
        this.setState({
            MobInfo:currentState
        });

    }

    onClick(e){
        submit(this.state.MobInfo);
    }

    render() {
        return(
            <div >
                <div style={title}>
                    <h2>Dialog Payment Gateway</h2>
                </div>
                <div style={body}>

                    <table style={tableStyle}>
                        <thead> <h2>Payment Details</h2></thead>
                        <tbody>
                        <tr style={cellStyle}>
                            <td>
                                <b><label>Phone Number</label></b><br/>
                                <input

                                    type='text'
                                    name="phone"
                                    value={this.state.MobInfo.phone}
                                    onChange={this.onChange}
                                />
                            </td>

                        </tr>
                        <tr style={cellStyle}>
                            <td>
                                <b><label>PIN Number</label></b><br/>
                                <input type='text'
                                       name="pin"
                                       placeholder="Four digit number"
                                       value={this.state.MobInfo.PIN}
                                       onChange={this.onChange}
                                 />
                            </td>

                        </tr>
                        <tr style={cellStyle}>
                            <td>
                                <b><label>Amount</label></b><br/>
                                  <label style={labelStyle}>{this.state.TrainDetail.ticketPrice} Rs/=</label>
                            </td>

                        </tr><br/>
                        <tr style={btnCellStyle}>
                            <td>
                                <button style={btnStyle} ><b>Pay</b></button>
                            </td>
                        </tr>
                        </tbody>
                    </table>
                </div>
            </div>


        );
    }
}

const title={
    textAlign:'center',
    postion:'absolute',

};
const body={
    fontSize:"17px",
    left:"420px",
    right:"320px",
    top:"100px",
    position:"absolute",
    textAlign:"left",
};

const tableStyle={
    width:"300px",
    position:"absolute",
    marginLeft:'10%',
    marginRight:'10%',

};

const cellStyle={
    height:'60px',
    border: '1px solid black'
};
const btnCellStyle={
    height:'60px',
    position:'fixed',
    left:'750px',

};

const btnStyle={
    background:'red',
    fontSize:"20px",
    color:'white',
    padding:'5px 8px',
    cursor:'pointer',
    float:'right',
    width:"80px",
    height:"50px"
};

const labelStyle={
    background:"white",
    fontWeight:"bold",
    fontSize:"20px",
    display:"block",
    width: "400px",
    height: "40px"
};
export default MobileView;
