import React,{Component} from 'react';
import Railway from './Railway.PNG';
import axios from 'axios';



const Statement=()=>{
    console.log(this.state.TrainDetail);
    console.log(this.state.Person);
};

class ConfirmSection extends Component
{
    constructor (props) {
        super(props);
        this.state = {

            TrainDetail: this.props.TrainDetail,
            Person:this.props.Person
        };

     }
    onSubmit(e){
        e.preventDefault();
        // const trainDetails = {
        //     trainDetails: this.state.TrainDetail
        // };
        // console.log(res=>res.json(trainDetails));
        // axios.post(`http://localhost:5000/citizen/addPerson`, {trainDetails})
        //     .then(res => {
        //         console.log(res);
        //         console.log(res.data);
        //     });
        //
        window.location.href='/bill';

    }


    render() {
        return(
            <div style={rightStyle}>
                <form>
                    <div><img src={Railway} width="200px" height="330px" alt="Railway"/></div>
                    <div style={downStyle}><button style={btnStyle} onClick={this.onSubmit}>View Bill</button></div>
                </form>
                {Statement()}
            </div>

       );

    }


}

const rightStyle={
    position:'fixed',

    left:'1300px',
    background:'LightGrey',
    width:'200px',
    height:'500px',
    textAlign:'left',
    border: '3px solid Black',
    borderTopStyle:'none',
    top: '190px',
};
const btnStyle={
    background:'red',
    color:'white',
    padding:'5px 8px',
    cursor:'pointer',
    float:'right',
    width:"110px",
    height:"50px"
};

const downStyle={
    margin: 'auto',
    width:"80px",
    padding:'10px',
    position: 'absolute',
    left:'70px',
    right:'80px',
    top: '350px',

};
export default ConfirmSection;
