import React,{Component} from 'react';

import axios from 'axios';

import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

import CustomerForm from "./CustomerForm";



// const stations = [
//     {label:"Wellawatte"},
//     {label:"Kollupitya"},
//     {label:"Bambalapitiya"},
//     {label:"Mount Lavania"}
// ];
// const  trainNumbers = [
//     {label:"T001"},
//     {label:"T002"},
//     {label:"T003"},
//     {label:"T004"},
// ];
// const  trainNames = [
//     {label:"WEL"},
//     {label:"KOL"},
//     {label:"BAM"},
//     {label:"MTLAV"},
//
// ];

let Stations =[];
let Trains = [];
let Start_End= [];



let TDetail=[];

const Statement=(props)=>{
     TDetail.push(props);
     console.log(props);
};


class TrainForm extends Component
{

    constructor (props) {
        super(props);
        var today = new Date();

        this.state = {
            TrainDetail:{
                start:null,
                end:null,
                trainNumber:null,
                trainName:null,
                date: today,
                ticketQuanity:null
            }
      };

        this.onDateChange = this.onDateChange.bind(this);
        this.onChange = this.onChange.bind(this);


        this.dateFormat=this.dateFormat.bind(this);
    }





    onChange(e){
        const {TrainDetail}=this.state;
        const currentState=TrainDetail;
        const {name,value}=e.target;
        currentState[name]=value;
        this.setState({
            TrainDetail:currentState
        });

    }


    dateFormat(date){
        var dd = date.getDate();
        var mm = date.getMonth()+1; //January is 0!
        var yyyy = date.getFullYear();

        if(dd<10) {
            dd = '0'+dd
        }

        if(mm<10) {
            mm = '0'+mm
        }

        return  dd + '/' + mm + '/' + yyyy;
    }

    onDateChange(date) {
        this.setState({
            TrainDetail:{date: date}
        });

    }


    render() {

        return(
            <div style={leftStyle}>
                <div style={{left:"200px",right:"300px", position:"absolute", width:"299px"}}>
                    <h3>Train details</h3>
                </div><br/><br/><br/>
                <table style={tableStyle}>
                    <tbody>

                        <tr style={cellStyle}>
                            <td>
                                <b><label>Date</label></b>
                            </td>
                            <td>
                                <DatePicker
                                    name="date"
                                    value={this.state.TrainDetail.date}
                                    selected={ this.state.TrainDetail.date}
                                    onChange={ this.onDateChange}
                                    dateFormat={this.dateFormat(this.state.TrainDetail.date)}
                                />
                            </td>
                        </tr>

                       <tr style={cellStyle}>
                           <td>
                               <b><label>Starting Point</label></b>
                           </td>
                           <td>
                               <select name ="start"
                                       style={{width:"400px" ,height:"30px"}}
                                       onChange = {this.onChange}>

                                   {
                                       <option value="" disabled selected>Select starting point</option>
                                   }

                                   {

                                       Stations.map((stat,i)=>{
                                           return <option value ={stat.name}>{stat.name}</option>
                                       })
                                   }
                               </select>


                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Destination Point</label></b>
                           </td>
                           <td>
                               <select name ="end"
                                       style={{width:"400px" ,height:"30px"}}
                                       onChange = {this.onChange}>
                                   {
                                       <option value="" disabled selected>Select destination point</option>
                                   }

                                   {
                                       Stations.map(stat=>{
                                           return <option key={stat._id} value ={stat.name}>{stat.name}</option>
                                       })
                                   }
                               </select>
                           </td>
                       </tr>

                       <tr style={cellStyle}>
                           <td>
                               <b><label>Train Number</label></b>
                           </td>
                           <td>
                               <select
                                           name ="trainNumber"
                                           style={{width:"400px" ,height:"30px"}}
                                           onChange = {this.onChange}>

                                   {
                                       <option value="" disabled selected>Select Train Number</option>
                                   }
                                   {/*{*/}
                                       {/*Trains.map(train=>{*/}
                                           {/*return <option value ={train.label}>{tNo.label}</option>*/}
                                       {/*})*/}
                                   {/*}*/}
                               </select>
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Train Name</label></b>
                           </td>
                           <td>
                               <select
                                   name="trainName"
                                   style={{width:"400px" ,height:"30px"}}
                                   onChange = {this.onChange}>

                                   {
                                       <option value="" disabled selected>Select Train Name</option>
                                   }
                                   {
                                       Trains.map(tNo=>{
                                           return <option value ={tNo.label}>{tNo.label}</option>
                                       })
                                   }
                               </select>
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Ticket Price</label></b>
                           </td>
                           <td>
                               <label>Ticket Price per person</label>
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Number of tickets</label></b>
                           </td>
                           <td>
                               <input type="text"
                                    name="ticketQuanity"
                                    value={this.state.TrainDetail.tickets}
                                    onChange={this.onChange}
                               />
                           </td>
                       </tr>

                    </tbody>

                </table>
                {Statement(this.state.TrainDetail)}
                <CustomerForm  {...this.props} TrainDetail={TDetail} />
            </div>

        );
    }
    componentWillMount() {
        axios.get('http://localhost:5000/info/5cddad037ed2b030a8007ff8/Stations')
            .then(res => {
                for(let i=0;i<=res.data.length;i++)
                {
                    Stations.push(res.data.pop())
                }
       });
        axios.get('http://localhost:5000/info/5cddad037ed2b030a8007ff8/Trains')
            .then(res => {
              for(let i=0;i<=res.data.length;i++)
              {
                  Trains.push(res.data.pop())
              }
        });
        axios.get('http://localhost:5000/info/5cddad037ed2b030a8007ff8/Start_End')
            .then(res => {
                for(let i=0;i<=res.data.length;i++)
                {
                    Start_End.push(res.data.pop())
                }

        });
        console.log( Trains[0]);
        console.log(Stations);
        console.log(Start_End);
    }
}




const leftStyle={
    position:'fixed',
    margin:'auto',
    background:'LightGrey',
    width:'600px',
    height:'500px',
    textAlign:'left',
    border: '3px solid Black',
    borderTopStyle:'none',
    top: '190px',
};
const cellStyle={
    height:'50px'
};

const tableStyle={
    width:"500px",
    marginLeft:'10%',
    marginRight:'10%'
};
export default TrainForm;
