import axios from 'axios'

let data='';

  const  validate =(id,card)=>{
    console.log(id);
    console.log(card);
    axios.get('http://localhost:5000/acc/'+id)
        .then(res => {
           data = res.data.Card;
            if(card.Name === data.cardHolder)
            {
                if(card.credit === data.cardNo && card.cvc === data.cvcNo)
                {
                    alert("payment sucess");
                }
            }
            console.log(data);
        });
};

  export default validate;
