import React,{Component} from 'react';

import Select from 'react-select';

import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';


const stations = [
    { label: "Wellawatte", value: 1 },
    { label: "Bambalapitya", value: 2 },
    { label: "Kollupitya", value: 3 },
];
const  trainNumbers = [
    { label: "T001", value: 1 },
    { label: "T002", value: 2 },

];
const  trainNames = [
    { label: "WEL", value: 1 },
    { label: "KOL", value: 2 },

];
let tprops;

class TrainForm extends Component
{

    constructor (props) {
        super(props);
        var today = new Date();

        this.props = {
            startDate: today,
            tickets:''

      };

        this.onChange = this.onChange.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.dateFormat=this.dateFormat.bind(this);


    }
    handleChange(date) {
        this.setState({
            startDate: date
        })
    }

    onChange(e){
        this.setState({  [e.target.name]: e.target.value }  );
    }


    dateFormat(date){
            var dd = date.getDate();
            var mm = date.getMonth()+1; //January is 0!
            var yyyy = date.getFullYear();

            if(dd<10) {
                dd = '0'+dd
            }

            if(mm<10) {
                mm = '0'+mm
            }

        return  dd + '/' + mm + '/' + yyyy;

    }


    render() {
        return(
            <div style={leftStyle}>
                <div style={{left:"200px",right:"300px", position:"absolute"}}>
                    <h3>Train details</h3>
                </div><br/><br/><br/>

                <b><label>Starting Point</label></b>
                <Select
                    width="1px"
                    placeholder="select Starting point"
                    options={ stations } />

            </div>

        );
    }
    componentWillMount(){
        tprops="test" ;
    }
}




const leftStyle={
    position:'fixed',
    margin:'auto',
    background:'LightGrey',
    width:'700px',
    height:'500px',
    textAlign:'left',
    border: '3px solid Black',
    borderTopStyle:'none',
    top: '190px',
};
const cellStyle={
    height:'50px'
};

const tableStyle={
    width:"500px",
    marginLeft:'10%',
    marginRight:'10%'
};
export default TrainForm;
export {tprops};
