import React,{Component} from 'react';

class MobileView extends Component{

    constructor(props){
        super(props);


        this.onChange.bind(this);
    }

    onChange(e){
        const {TrainDetail}=this.state;
        const currentState=TrainDetail;
        const {name,value}=e.target;
        currentState[name]=value;
        this.setState({
            TrainDetail:currentState
        });

    }
    render() {
        return(
            <div >
                <div style={title}>
                    <h2>Dialog Payment Gateway</h2>
                </div>
                <div style={body}>

                    <table style={tableStyle}>
                        <thead> <h2>Payment Details</h2></thead>
                        <tbody>
                        <tr style={cellStyle}>
                            <td>
                                <b><label>Phone Number</label></b><br/>
                                <input

                                    type='text'
                                    name="contactNo"
                                />
                            </td>

                        </tr>
                        <tr style={cellStyle}>
                            <td>
                                <b><label>PIN Number</label></b><br/>
                                <input type='text'
                                       name="pin"
                                       placeholder="Four digit number"
                                 />
                            </td>

                        </tr>
                        <tr style={cellStyle}>
                            <td>
                                <b><label>Amount</label></b><br/>
                                <input type='text'
                                       name="pin"
                                       placeholder="Amount in Rs/="
                                />
                            </td>

                        </tr><br/>
                        <tr style={btnCellStyle}>
                            <td>
                                <button style={btnStyle} ><b>Pay</b></button>
                            </td>
                        </tr>
                        </tbody>
                    </table>
                </div>
            </div>


        );
    }
}

const title={
    textAlign:'center',
    postion:'absolute',

};
const body={
    fontSize:"17px",
    left:"420px",
    right:"320px",
    top:"100px",
    position:"absolute",
    textAlign:"left",
};

const tableStyle={
    width:"300px",
    position:"absolute",
    marginLeft:'10%',
    marginRight:'10%',

};

const cellStyle={
    height:'60px',
    border: '1px solid black'
};
const btnCellStyle={
    height:'60px',
    position:'fixed',
    left:'750px',

};

const btnStyle={
    background:'red',
    fontSize:"20px",
    color:'white',
    padding:'5px 8px',
    cursor:'pointer',
    float:'right',
    width:"80px",
    height:"50px"
};

export default MobileView;
