import React,{Component} from 'react';

import axios from 'axios';

let details={};
let id='';
class CreditView extends Component{
    constructor(props)
    {
        super(props);
        this.state={
            TrainDetail:'',
            Person:'',
            ticketId:''
        };

        id=window.location.href.substring(27,40);
        id=id.substring(2,40);

        console.log();
        axios.get('http://localhost:5000/citizen/'+id)
            .then(res => {
                details = res.data;
                console.log(details);
                this.setState({TrainDetail:details.TrainDetail} );
                this.setState({Person:details.Person} );
                this.setState({ticketId:details.id});
            });
             console.log(this.state.TrainDetail)
    }

    render() {
       return(
           <div >
               <div style={title}>
                   <h2>Sampath Bank Payment Gateway</h2>
               </div>
               <div style={body}>

                   <table style={tableStyle}>
                       <thead> <h2>Payment Details</h2></thead>
                       <tbody>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Card holder name</label></b><br/>
                               <input type='text' name="Name"/>
                           </td>

                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Amount</label></b><br/>
                               <input
                                   type='text'
                                   placeholder="Amount in Rs/="
                                   name="amt">
                                   {/*{this.state.TrainDetail.ticketQuanity}*/}

                               </input>
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b>Credit Card Number</b><br/>
                               <input type='text' name="credit"/>
                           </td>
                           <td>
                               <b><label>CVC Number</label></b><br/>
                               <input type='text'
                                      name="cvc"
                                      placeholder="3 digit number at back of card"
                               />
                           </td>
                       </tr>
                       <tr style={btnCellStyle}>
                           <td>
                               <button style={btnStyle} ><b>Pay</b></button>
                           </td>
                       </tr>
                       </tbody>
                   </table>
               </div>
           </div>


       );
    }
}

const title={
   textAlign:'center',
    postion:'absolute',

};
const body={
    fontSize:"17px",
    left:"120px",
    right:"120px",
    top:"100px",
    position:"absolute",
    textAlign:"left",
};

const tableStyle={
    width:"300px",
    position:"absolute",
    marginLeft:'10%',
    marginRight:'10%',

};

const cellStyle={
    height:'60px',

    border: '1px solid black'
};
const btnCellStyle={
    height:'60px',
    border: '1px solid black',
    left:'200px',
    right:'200px'
};

const btnStyle={
    background:'red',
    fontSize:"20px",
    color:'white',
    padding:'5px 8px',
    cursor:'pointer',
    float:'right',
    width:"80px",
    height:"50px"
};

export default CreditView;
