import React,{Component} from 'react';

import Select from 'react-select';


import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

import CustomerForm from "./CustomerForm";


const stations = [
    { label: "Wellawatte", value: 1 },
    { label: "Bambalapitya", value: 2 },
    { label: "Kollupitya", value: 3 },
];
const  trainNumbers = [
    { label: "T001", value: 1 },
    { label: "T002", value: 2 },

];
const  trainNames = [
    { label: "WEL", value: 1 },
    { label: "KOL", value: 2 },

];
let TDetail=[];

const Statement=(props)=>{
     TDetail.push(props);
         console.log(props);
};





class TrainForm extends Component
{

    constructor (props) {
        super(props);
        var today = new Date();

        this.state = {
            TrainDetail:{
                start:{stations},
                end:null,
                trainNumber:null,
                trainName:null,
                date: today,
                ticketQuanity:null
            }
      };

        this.onDateChange = this.onDateChange.bind(this);
        this.onChange = this.onChange.bind(this);
        this.change= this.change().bind(this);

        this.dateFormat=this.dateFormat.bind(this);
    }





    onChange(e){
        const {TrainDetail}=this.state;
        const currentState=TrainDetail;
        const {name,value}=e.target;
        currentState[name]=value;
        this.setState({
            TrainDetail:currentState
        });

    }




    onDateChange(date) {
        this.setState({
            TrainDetail:{date: date}
        });

    }


    dateFormat(date){
        var dd = date.getDate();
        var mm = date.getMonth()+1; //January is 0!
        var yyyy = date.getFullYear();

        if(dd<10) {
            dd = '0'+dd
        }

        if(mm<10) {
            mm = '0'+mm
        }

        return  dd + '/' + mm + '/' + yyyy;

    }




    change(event){
       this.setState({value: event.target.value});

    }

    render() {
        return(
            <div style={leftStyle}>
                <div style={{left:"200px",right:"300px", position:"absolute", width:"299px"}}>
                    <h3>Train details</h3>
                </div><br/><br/><br/>
                <table style={tableStyle}>
                    <tbody>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Starting Point</label></b>
                           </td>
                           <td>
                               <select id="lang" onChange={this.change} value={this.state.value}>
                                   <option value="select">Select</option>
                                   <option value="Java">Java</option>
                                   <option value="C++">C++</option>
                               </select>


                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Destination Point</label></b>
                           </td>
                           <td>
                               <Select width="500px"
                                       placeholder="Select Destination point"
                                       options={ stations}
                                       name="end"
                                       // value={this.state.trainDetails.end}
                                       // onChange={this.onPickChange}
                               />
                           </td>
                       </tr>

                       <tr style={cellStyle}>
                           <td>
                               <b><label>Train Number</label></b>
                           </td>
                           <td>
                               <Select autosize={true}
                                       placeholder="Select Train Numbers"
                                       options={ trainNumbers }
                                       name="tNo"
                                       // value={this.state.trainDetails.tNo}
                                       // onChange={this.onPickChange}
                               />
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Train Name</label></b>
                           </td>
                           <td>
                               <Select autosize={true}
                                       placeholder="Select Train Names"
                                       options={ trainNames }
                                       name="tName"
                                       // value={this.state.trainDetails.tName}
                                       // onChange={this.onChange}
                               />
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Ticket Price</label></b>
                           </td>
                           <td>
                               <label>Ticket Price per person</label>
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Date</label></b>
                           </td>
                           <td>
                               <DatePicker
                                   onSelect={ this.state.TrainDetail.date}
                                   name="date"
                                   value={this.state.TrainDetail.date}
                                   onChange={ this.onDateChange}
                                   dateFormat={this.dateFormat(this.state.TrainDetail.date)}

                               />
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Number of tickets</label></b>
                           </td>
                           <td>
                               <input type="text"
                                    name="ticketQuanity"
                                    value={this.state.TrainDetail.tickets}
                                    onChange={this.onChange}
                               />
                           </td>
                       </tr>
                    </tbody>

                </table>

                {Statement(this.state.TrainDetail)}
                <CustomerForm  {...this.props} TrainDetail={TDetail} />
            </div>

        );
    }

}




const leftStyle={
    position:'fixed',
    margin:'auto',
    background:'LightGrey',
    width:'600px',
    height:'500px',
    textAlign:'left',
    border: '3px solid Black',
    borderTopStyle:'none',
    top: '190px',
};
const cellStyle={
    height:'50px'
};

const tableStyle={
    width:"500px",
    marginLeft:'10%',
    marginRight:'10%'
};
export default TrainForm;
