import React,{Component} from 'react';
import ConfirmSection from "./ConfirmSection";


class CustomerForm extends Component
{
    constructor (props) {
        super(props);

        this.state = {

            TrainDetail: this.props.TDetail[0]

        }
    }

    render() {
        return(
            <div style={rightStyle}>
                <div style={{left:"100px",right:"200px", position:"absolute"}}>
                    <h3 style={{textAlign:'center'}}>Customer Details</h3>
                </div><br/><br/><br/>

                <table style={tableStyle}>
                    <tbody>
                    <tr style={cellStyle}>
                        <td>
                            <b><label>Name</label></b>
                        </td>
                        <td>
                            <input type='text' name="Name"/>
                        </td>
                    </tr>
                    <tr style={cellStyle}>
                        <td>
                            <b><label>NIC No</label></b>
                        </td>
                        <td>
                            <input
                                type='text'
                                placeholder="NIC no should be of 10 digits"
                                name="Name"/>
                        </td>

                    </tr>
                    <tr style={cellStyle}>
                        <td>
                            <b><label>Contact Number</label></b>
                        </td>
                        <td>
                            <input type='text' name="contact"/>
                        </td>
                    </tr>
                    <tr style={cellStyle}>
                        <td>
                            <b><label>Email</label></b>
                        </td>
                        <td>
                            <input type='text' name="email"/>
                        </td>
                    </tr>
                    </tbody>
                </table>

                <ConfirmSection  {...this.props}  TrainDetail={this.state.TrainDetail}/>
                {
                    console.log(this.state.TDetail)
                }
            </div>);
    }

}

const rightStyle={
    position:'fixed',
    margin:'auto',
    right:'400px',
    backgroundColor:'LightGrey',
    width:'600px',
    height:'500px',
    textAlign:'left',
    border: '3px solid Black',
    borderTopStyle:'none',
    top: '190px',
};
const cellStyle={
    height:'50px'
};
const tableStyle={
  width:"500px",
  marginLeft:'10%',
  marginRight:'10%'
};

export default CustomerForm;
