import axios from 'axios'

let data='';

const  validate =(id,mobile)=>{
    console.log(id);
    console.log(mobile);
    axios.get('http://localhost:5000/acc/'+id)
        .then(res => {
            data = res.data.Mobile;
            if(mobile.phone === data.phone)
            {
                if(mobile.PIN === data.PIN)
                {
                    alert("Payment successful");
                    data=res.data.Account;
                    withdraw(id,data.Balance);
                }
            }
            console.log(data);
        });
};

const withdraw=(id,amount)=>{
    console.log(id);
    console.log(amount)
};

// const sendEmail=(message)=>{
//     console.log(message)
// };

export default validate;
