import React,{Component} from 'react';

import Select from 'react-select';


import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

import CustomerForm from "./CustomerForm";


const stations = [
    { label: "Wellawatte", value: 1 },
    { label: "Bambalapitya", value: 2 },
    { label: "Kollupitya", value: 3 },
];
const  trainNumbers = [
    { label: "T001", value: 1 },
    { label: "T002", value: 2 },

];
const  trainNames = [
    { label: "WEL", value: 1 },
    { label: "KOL", value: 2 },

];
let TDetail=[];

const Statement=(props)=>{
     TDetail.push(props);
         console.log(props);
};





class TrainForm extends Component
{

    constructor (props) {
        super(props);
        var today = new Date();

        this.state = {
            TrainDetail:{
                start:null,
                end:null,
                trainNumber:null,
                trainName:null,
                date: today,
                ticketQuanity:null
            }
      };

        this.onDateChange = this.onDateChange.bind(this);
        this.onChange = this.onChange.bind(this);
        this.onPickChange = this.onPickChange.bind(this);

        this.dateFormat=this.dateFormat.bind(this);
    }





    onChange(e){
        const {TrainDetail}=this.state;
        const currentState=TrainDetail;
        const {name,value}=e.target;
        currentState[name]=value;
        this.setState({
            TrainDetail:currentState
        });

    }


    dateFormat(date){
        var dd = date.getDate();
        var mm = date.getMonth()+1; //January is 0!
        var yyyy = date.getFullYear();

        if(dd<10) {
            dd = '0'+dd
        }

        if(mm<10) {
            mm = '0'+mm
        }

        return  dd + '/' + mm + '/' + yyyy;
    }

    onDateChange(date) {
        this.setState({
            TrainDetail:{date: date}
        });

    }





       onPickChange(e){
        const {TrainDetail}=this.state;
            const currentState=TrainDetail;
            const {id,value}=e.target;
            currentState[id]=value;
            this.setState({
                TrainDetail:currentState
            });
    }


    render() {
        return(
            <div style={leftStyle}>
                <div style={{left:"200px",right:"300px", position:"absolute", width:"299px"}}>
                    <h3>Train details</h3>
                </div><br/><br/><br/>
                <table style={tableStyle}>
                    <tbody>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Starting Point</label></b>
                           </td>
                           <td>
                               <select id ="start" style={{width:"400px" ,height:"30px"}}   onChange = {this.onPickChange}>
                                   {
                                       stations.map(stat=>{
                                           return <option value ={stat.label}>{stat.label}</option>
                                       })
                                   }
                               </select>


                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Destination Point</label></b>
                           </td>
                           <td>
                               <select id ="end" style={{width:"400px" ,height:"30px"}}   onChange = {this.onPickChange}>
                                   {
                                       stations.map(stat=>{
                                           return <option value ={stat.label}>{stat.label}</option>
                                       })
                                   }
                               </select>
                           </td>
                       </tr>

                       <tr style={cellStyle}>
                           <td>
                               <b><label>Train Number</label></b>
                           </td>
                           <td>
                               <select id ="tNo" style={{width:"400px" ,height:"30px"}}   onChange = {this.onPickChange}>
                                   {
                                       trainNumbers.map(tNo=>{
                                           return <option value ={tNo.label}>{tNo.label}</option>
                                       })
                                   }
                               </select>
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Train Name</label></b>
                           </td>
                           <td>
                               <select id ="tName" style={{width:"400px" ,height:"30px"}}   onChange = {this.onPickChange}>
                                   {
                                       trainNames.map(tNo=>{
                                           return <option value ={tNo.label}>{tNo.label}</option>
                                       })
                                   }
                               </select>
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Ticket Price</label></b>
                           </td>
                           <td>
                               <label>Ticket Price per person</label>
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Date</label></b>
                           </td>
                           <td>
                               <DatePicker
                                   selected={ this.state.TrainDetail.date}
                                   name="date"
                                   value={this.state.TrainDetail.date}
                                   onChange={ this.onDateChange}
                                   dateFormat={this.dateFormat(this.state.TrainDetail.date)}
                               />
                           </td>
                       </tr>
                       <tr style={cellStyle}>
                           <td>
                               <b><label>Number of tickets</label></b>
                           </td>
                           <td>
                               <input type="text"
                                    name="ticketQuanity"
                                    value={this.state.TrainDetail.tickets}
                                    onChange={this.onChange}
                               />
                           </td>
                       </tr>
                    </tbody>

                </table>

                {Statement(this.state.TrainDetail)}
                <CustomerForm  {...this.props} TrainDetail={TDetail} />
            </div>

        );
    }

}




const leftStyle={
    position:'fixed',
    margin:'auto',
    background:'LightGrey',
    width:'600px',
    height:'500px',
    textAlign:'left',
    border: '3px solid Black',
    borderTopStyle:'none',
    top: '190px',
};
const cellStyle={
    height:'50px'
};

const tableStyle={
    width:"500px",
    marginLeft:'10%',
    marginRight:'10%'
};
export default TrainForm;
