import React,{Component} from 'react';
import { ScrollTo, ScrollArea } from "react-scroll-to";;

class BillView extends Component
{

    render() {
        return(

                <div style={title}>
                    <ScrollTo>
                        <ScrollArea></ScrollArea>
                    </ScrollTo>
                </div>

        );

    }
}
const title={
    textAlign:"center"
};
const body={
   fontSize:"25px",
   left:"1200px",
   textAlign:"left",
   right:"1200px"
};
const tableStyle={
    position:"fixed",
    width:"500px",
    marginLeft:'10%',
    marginRight:'10%',
    border: '1px solid black',
    left:"400px"
};

const cellStyle={
    height:'60px',
    backgroundColor:"White",
    border: '1px solid black'
};
const HeadStyle={
    height:'60px',
    width:'210px',
    border: '1px solid black',
    backgroundColor:"Orange",
    fontColor:"white"
};

const btnStyle={
    background:'red',
    fontSize:"20px",
    color:'white',
    padding:'5px 8px',
    cursor:'pointer',
    float:'right',
    width:"150px",
    height:"70px"
};

const downStyle={
    margin: 'auto',
    width:"80px",
    padding:'10px',
    position: 'absolute',
    left:'960px',
    right:'80px',
    top: '250px',

};

export default BillView
